package com.nineya.spring.aop;import org.springframework.context.annotation.AnnotationConfigApplicationContext;public class SpringAopMain {    public static void main(String[] args) {        System.out.println("## 创建容器");        AnnotationConfigApplicationContext context = new AnnotationConfigApplicationContext(AopConfiguration.class );        System.out.println("## 通过class取得bean");        BeanObject beanObject = context.getBean(BeanObject.class);        beanObject.run("newName");        context.getBean(AopHandler.class);    }}