package com.nineya.spring.bean.cycle_plus;import com.nineya.spring.bean.cycle_plus.entity.TwoBean;import com.nineya.spring.bean.cycle_plus.entity.OneBean;import com.nineya.spring.bean.cycle_plus.entity.ThreeBean;import org.springframework.context.annotation.AnnotationConfigApplicationContext;public class BeanCyclePlusMain {    public static void main(String[] args) {        System.out.println("## 创建容器");        AnnotationConfigApplicationContext context = new AnnotationConfigApplicationContext();        System.out.println("## 扫描Bean注解");        context.scan(BeanCyclePlusMain.class.getPackage().getName());        context.refresh();        System.out.println("## 通过class取得oneBean");        OneBean oneBean = context.getBean(OneBean.class);        System.out.println(oneBean + " : " + oneBean.hashCode());        System.out.println("getTwoBean : " + oneBean.getTwoBean());        System.out.println("getThreeBean : " + oneBean.getThreeBean());        System.out.println("## 通过class取得twoBean");        TwoBean twoBean = context.getBean(TwoBean.class);        System.out.println(twoBean + " : " + twoBean.hashCode());        System.out.println("getOneBean : " + twoBean.getOneBean());        System.out.println("## 通过class取得threeBean");        ThreeBean threeBean = context.getBean(ThreeBean.class);        System.out.println(threeBean + " : " + threeBean.hashCode());        System.out.println("getOneBean : " + threeBean.getOneBean());    }}