package com.nineya.spring.bean.cycle_plus.entity;import org.springframework.context.annotation.Lazy;import org.springframework.stereotype.Component;import javax.annotation.Resource;@Lazy@Componentpublic class OneBean {    @Resource    private TwoBean twoBean;    @Resource    private ThreeBean threeBean;    public OneBean() {        System.out.println("OneBean：实例化");    }    public TwoBean getTwoBean() {        return twoBean;    }    public void setTwoBean(TwoBean twoBean) {        System.out.println("setTwoBean：" + twoBean);        this.twoBean = twoBean;    }    public ThreeBean getThreeBean() {        return threeBean;    }    public void setThreeBean(ThreeBean threeBean) {        System.out.println("setThreeBean：" + threeBean);        this.threeBean = threeBean;    }}