package com.nineya.spring.bean.lifecycle.config;import com.nineya.spring.bean.lifecycle.entity.BeanProcess;import com.nineya.spring.bean.lifecycle.processor.NineyaBeanFactoryPostProcessor;import com.nineya.spring.bean.lifecycle.processor.NineyaBeanPostProcessor;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;@Configurationpublic class BeanLifecycleConfiguration {    @Bean(value = "test", initMethod = "initMethod", destroyMethod = "destroyMethod")    public BeanProcess beanProcess() {        BeanProcess beanProcess = new BeanProcess();        beanProcess.setName("test");        return beanProcess;    }    @Bean    public NineyaBeanPostProcessor nineyaBeanPostProcessor() {        return new NineyaBeanPostProcessor();    }    @Bean    public NineyaBeanFactoryPostProcessor nineyaBeanFactoryPostProcessor() {        return new NineyaBeanFactoryPostProcessor();    }}