package com.nineya.spring.entity;import lombok.Getter;import lombok.Setter;import lombok.ToString;import org.springframework.beans.factory.BeanNameAware;import org.springframework.beans.factory.DisposableBean;import org.springframework.beans.factory.InitializingBean;import org.springframework.stereotype.Component;import javax.annotation.PostConstruct;import javax.annotation.PreDestroy;@Getter@Setter@ToString@Component("test")public class BeanProcess implements BeanNameAware, InitializingBean, DisposableBean {    private String name;    @PostConstruct    public void initMethod() {        System.out.println("Bean InitMethodName: name = " + name);    }    @PreDestroy    public void destroyMethod() {        System.out.println("Bean DestroyMethodName: name = " + name);    }    @Override    public void destroy() {        System.out.println("DisposableBean: name = " + name);    }    @Override    public void setBeanName(String name) {        System.out.println("BeanNameAware Name: " + name + ", Parameter Name: " + this.name);    }    @Override    public void afterPropertiesSet() throws Exception {        System.out.println("InitializingBean Name: " + name);    }}